package com.qiang.baselib.base;import io.reactivex.disposables.CompositeDisposable;import io.reactivex.disposables.Disposable;/** * @author lixiqiang * @dateï¼š 2019/9/10 0010 */public class BasePresenter<V extends BaseView> {    protected V view;    private CompositeDisposable compositeDisposable;    public void attachView(V mView) {        this.view = mView;    }    public void addDisposable(Disposable disposable) {        if (compositeDisposable == null) {            compositeDisposable = new CompositeDisposable();        }        compositeDisposable.add(disposable);    }    public void detachView() {        if (compositeDisposable != null && !compositeDisposable.isDisposed()) {            compositeDisposable.dispose();            compositeDisposable = null;            view = null;        }    }}