package com.qiang.home.activity;import android.content.Intent;import android.os.Bundle;import android.view.View;import android.widget.TextView;import com.qiang.baselib.base.BaseActivity;import com.qiang.baselib.base.BasePresenter;import com.qiang.baselib.listener.NoDoubleClickListener;import com.qiang.home.R;public class MainActivity extends BaseActivity {    TextView tvMain1;    TextView tvMain2;    @Override    protected BasePresenter initPresenter() {        return null;    }    @Override    protected int getLayoutId() {        return R.layout.activity_main;    }    @Override    protected void initView(Bundle savedInstanceState) {        tvMain1 = findViewById(R.id.tv_main1);        tvMain2 = findViewById(R.id.tv_main2);        tvMain1.setOnClickListener(listener);        tvMain2.setOnClickListener(listener);    }    public NoDoubleClickListener listener = new NoDoubleClickListener() {        @Override        protected void onNoDoubleClick(View view) {            if (view.getId() == R.id.tv_main1) {                Intent intent = new Intent(MainActivity.this, Main1Activity.class);                startActivity(intent);            }        }    };}