package com.qiang.home.fragment;import android.os.Bundle;import android.view.View;import androidx.recyclerview.widget.LinearLayoutManager;import androidx.recyclerview.widget.RecyclerView;import com.qiang.baselib.base.BaseFragment;import com.qiang.baselib.base.BasePresenter;import com.qiang.home.R;import com.qiang.home.adapter.RecyclerAdapter;import java.util.ArrayList;/** * @author lixiqiang * @date：2019/10/23 0023 */public class ItemFragment extends BaseFragment {    RecyclerView recyclerView;    @Override    protected int getLayoutId() {        return R.layout.fragment_item;    }    @Override    protected BasePresenter initPresenter() {        return null;    }    @Override    protected void initView(Bundle savedInstanceState, View rootView) {        recyclerView = rootView.findViewById(R.id.recycler_view);        ArrayList<String> list = new ArrayList<>();        RecyclerView.LayoutManager manager = new LinearLayoutManager(mContext, RecyclerView.VERTICAL, false);        recyclerView.setLayoutManager(manager);        RecyclerAdapter adapter = new RecyclerAdapter();        recyclerView.setAdapter(adapter);        for (int i = 0; i < 10; i++) {            list.add("帅过吴彦祖");            list.add("美过刘亦菲");            list.add("我是彭于晏");        }        adapter.setNewData(list);    }    @Override    protected void initData() {    }    public static ItemFragment newInstance(int type) {        Bundle args = new Bundle();        args.putInt("type", type);        ItemFragment fragment = new ItemFragment();        fragment.setArguments(args);        return fragment;    }}